import Nav from "./Nav"
import { Link } from "react-router-dom";
import { useState } from "react";
import PostLoginData from "../service/PostLoginData"
import {isLoggedIn, setIsLoggedIn } from "../global/Loginvalue";
import { Navigate } from "react-router-dom";


const Login = () => {


    const [logindata , setLogindata] = useState({
        email: '',
        password: ''
    })

    const updateLoginData = (event,field) => {
        setLogindata({...logindata,[field]:event.target.value})
    }



    const sendLoginData = (event) => {
        event.preventDefault();
        PostLoginData(logindata).then((res) => {
           console.log("login sucess")
           console.log(res)
           if(res.data === ""){
            <Navigate to="/login" />
           }
           else{
            {setIsLoggedIn(true)};
            <Navigate to="/login/courses" />
           }
        }).catch((error) => {
            console.log(error);
            console.log("error in login")
        });
    }
    

    return (

        <div>
            <Nav />
           

                <div className="flex relative bg-gradient-to-t from-pink-300 via-purple-300 to-indigo-400 h-screen justify-center">

                    
                        <div className="flex flex-col items-center justify-center px-6 py-8 mx-auto md:h-screen lg:py-0">
                            <div className="w-full bg-white rounded-lg shadow dark:border md:mt-0 sm:max-w-md xl:p-0 dark:bg-gray-800 dark:border-gray-700">
                                <div className="p-6 space-y-4 md:space-y-6 sm:p-8">
                                    <h1 className="text-xl font-bold leading-tight tracking-tight text-gray-900 md:text-2xl dark:text-white">
                                        Sign in to your account
                                    </h1>
                                    <form className="space-y-4 md:space-y-6" action="#">
                                        <div>
                                            <label for="email" className="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Your email</label>
                                            <input type="email" name="email" id="email" className="bg-gray-50 border border-gray-300 text-gray-900 sm:text-sm rounded-lg focus:ring-primary-600 focus:border-primary-600 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500" placeholder="enter email" required="" 
                                                    onChange={(e) => {updateLoginData(e,"email")}}/>
                                        </div>
                                        <div>
                                            <label for="password" className="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Password</label>
                                            <input type="password" name="password" id="password" placeholder="••••••••" className="bg-gray-50 border border-gray-300 text-gray-900 sm:text-sm rounded-lg focus:ring-primary-600 focus:border-primary-600 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500" required="" 
                                                    onChange={(e) => updateLoginData(e,"password")}/>
                                        </div>
                                        <div className="flex items-center justify-between">                                           
                                            <a href="#" className="text-sm font-medium text-primary-600 hover:underline dark:text-primary-500">Forgot password?</a>
                                        </div>
                                        <button type="submit" className="w-full text-white bg-blue-500  font-medium rounded-lg text-sm px-5 py-2.5 text-center  hover:bg-[#87acec] "
                                                onClick={(e) => sendLoginData(e)} >Login</button>
                                        <p className="text-sm font-light text-gray-500 dark:text-gray-400">
                                            Don’t have an account yet? <Link to="/signup" className="font-medium text-primary-600 hover:underline dark:text-primary-500">Sign up</Link>
                                        </p>
                                    </form>
                                </div>
                            </div>
                        </div>
                 

                </div>


            </div>
      
    );
}

export default Login;
