import images from "../Assets/images.jpg"
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import { LoginValueContext } from "../global/Loginvalue";
import { useContext } from "react";


const Nav = () => {

    const {loggedInValue, setLoggedInValue} = useContext(LoginValueContext)

    const navigate = useNavigate();
    let url = '';
    if(isLoggedIn === false){
        url = "/login"
    }
    else{
        url = "/login/courses"
    }

    return (

        <div class="flex flex-col">
            <header class="bg-[#FCFCFC]">
                <nav class="flex justify-between items-center w-[92%]  mx-auto">
                    <div>
                        <img class="w-40 cursor-pointer" src={images} alt="..." />
                    </div>
                    <div
                        class="nav-links duration-500 md:static absolute bg-green md:min-h-fit min-h-[60vh] left-0 top-[-100%] md:w-auto  w-full flex items-center px-5">
                        <ul class="flex md:flex-row flex-col md:items-center md:gap-[4vw] gap-8">
                            <li>
                                <Link class="hover:text-gray-500" to="/home">Home</Link>
                            </li>
                            <li>
                                <Link class="hover:text-gray-500" to="/contact">Contact</Link>
                            </li>
                            <li>
                                <Link class="hover:text-gray-500" to="/aboutus">About us</Link>
                            </li>
                            <li>
                                <Link class="hover:text-gray-500" to= {url} >Courses</Link>
                            </li>
                        </ul>
                    </div>
                    <div class="flex items-center gap-4">
                        <button class="bg-[#2387F6] text-white px-5 py-2 rounded-full hover:bg-[#87acec]"
                                onClick={() => navigate("/login")} >Login</button>
                        <button  class="bg-[#2387F6] text-white px-5 py-2 rounded-full hover:bg-[#87acec]"
                                onClick={() => navigate("/signup")} >Sign up</button>
                    </div>
                </nav>
            </header>
        </div>
    );
}

export default Nav;
